html:
{% set company = frappe.get_doc("Global Defaults").default_company %}
{% set address = frappe.get_doc("Inn Hotels Setting").address %}
{% set phone = frappe.get_doc("Inn Hotels Setting").phone %}
{% set customer = frappe.get_doc("Customer", doc.customer_id) %}
{% set accumulator1 = namespace(debit = 0.0) %}
{% set accumulator2 = namespace(credit = 0.0) %}
{% set accumulator3 = namespace(refund = 0.0) %}
{% set room_charge = {} %}
{% set package = {} %}

{% for row in doc.folio_transaction %}
  {% if row.sub_folio == 'A' and row.is_void == 0 %}
    {% if row.flag == 'Debit' %}
      {% set accumulator1.debit = accumulator1.debit + row.amount %}
    {% elif row.flag == 'Credit' %}
      {% set accumulator2.credit = accumulator2.credit + row.amount %}
    {% endif %}

    {% if row.transaction_type == 'Room Charge' %}
      {% set _ = room_charge.update({row.ftb_id: row.amount}) %}
    {% elif row.transaction_type == 'Package' %}
      {% set _ = package.update({row.ftb_id: row.amount}) %}
    {% endif %}
  {% endif %}
{% endfor %}
<div class="container">
<div class="row">
<div class="col-xs-1" style="padding-right:0px">
    <img src="{{ frappe.db.get_value('Company', doc.company, 'company_logo') or '' }}" alt="" align="left" height="95px" />
</div>
<div class="col-xs-5" style="padding-left:0px">
    <h3><b>{{company|upper}}</b></h3>
    <p>{{address}}</p>
    <p>Phone: {{phone}}</p>
    <br/>
</div>
</div>
</div>
<hr style='border:0.5px solid black; width:100%; margin:5px 0px 10px 0px;'>
{% for row in doc.folio_transaction %}
  {% if row.sub_folio == 'A' and row.is_void == 0 %}
    {% if row.transaction_type != 'Room Charge' and row.transaction_type != 'Package'%}
      {% if row.ftb_id in room_charge %}
        {% set _ = room_charge.update({row.ftb_id: room_charge[row.ftb_id] + row.amount}) %}
      {% elif row.ftb_id in package %}
        {% set _ = package.update({row.ftb_id: package[row.ftb_id] + row.amount}) %}
      {% endif %}
    {% endif %}
  {% endif %}
{% endfor %}

<div class= "row">
  <div class= "col-xs-4">
    <div class= "col-xs-3" style= "padding-left:0px;"><b>Customer:</b></div>
    <div class= "col-xs-9">{{ doc.customer_id | upper }}</div>
    <div class= "col-xs-3" style= "padding-left:0px;"><b>Type:</b></div>
    <div class= "col-xs-9">{{ doc.type }}</div>
    <div class= "col-xs-3" style= "padding-left:0px;"><b>Address:</b></div>
    {% if customer.primary_address != None %}
    <div class= "col-xs-9">{{ customer.primary_address }}</div>
    {% else %}
    <div class= "col-xs-9"></div>
    {% endif%}
    </div>
    <div class= "col-xs-8">
      <table class= "table table-bordered" style= "margin-top:-1px;">
        <tr>
          <th>Reservation</th>
          <th>Check In</th>
          <th>Check Out</th>
          <th>Folio</th>
          <th>Balance</th>
        </tr>
        <tr>
          {% if doc.reservation_id != None %}
            {% set reservation = frappe.get_doc("Inn Reservation", doc.reservation_id) %}
            <td>{{doc.reservation_id}}</td>
            <td>{{(reservation.arrival|string)[:11]}}</td>
            <td>{{(reservation.departure|string)[:11]}}</td>
          {% else %}
            <td></td>
            <td>{{(doc.open|string)[:11]}}</td>
            <td>{{doc.close}}</td>
          {% endif %}
          <td>{{doc.name}}</td>
          <td>{{ frappe.format_value(accumulator2.credit-accumulator1.debit, {"fieldtype":"Currency"}) }}</td>
        </tr>
      </table>
    </div>
  </div>

<div class= "row">
  <div class= "col-xs-12">
    <table class= "table table-bordered">
      <tr>
        <th class= "col-xs-2">Date</th>
        <th class= "col-xs-2">Type</th>
        <th class= "col-xs-1">Mode of Payment</th>
        <th class= "col-xs-3">Description/Remark</th>
        <th class= "col-xs-2">Debit</th>
        <th class= "col-xs-2">Credit</th>
      </tr>

      {% for row in doc.folio_transaction %}
        {% if row.sub_folio == 'A' and row.is_void == 0 %}
          {% if row.transaction_type not in ['Room Charge Tax/Service', 'Breakfast Charge Tax/Service', 'Breakfast Charge', 'Package Tax', 'Commission'] %}
            <tr>
              <td>{{row.audit_date}}</td>
              <td>{{row.transaction_type}}</td>
              {% if row.mode_of_payment != None %}
                <td>{{row.mode_of_payment}}</td>
              {% else %}
                <td></td>
              {% endif %}
              <td>{{row.remark}}</td>

              {% if row.flag == 'Debit' %}
                <td class= "text-right">
                  {% if row.transaction_type == 'Room Charge' %}
                    {{ frappe.format_value(room_charge[row.ftb_id], {"fieldtype":"Currency"}) }}
	          {% elif row.transaction_type == 'Package' %}
                    {{ frappe.format_value(package[row.ftb_id], {"fieldtype":"Currency"}) }}
                  {% else %}
                    {{ frappe.format_value(row.amount, {"fieldtype":"Currency"}) }}
	          {% endif %}
                </td>
                <td class= "text-center">-</td> 
              {% elif row.flag == 'Credit' %}
                <td class= "text-center">-</td>
                <td class= "text-right">
                  {{ frappe.format_value(row.amount, {"fieldtype":"Currency"}) }}
                </td>
              {% endif %}

              {% if row.transaction_type == 'Refund' %}
                {% set accumulator3.refund = accumulator3.refund + row.amount %}
              {% endif %}
            </tr>
          {% endif %}
        {% endif %}
      {% endfor %}

      <tr>
        <td colspan="4" class= "text-right"><b>Total</b></td>
        <td class= "text-right">
          <b>{{ frappe.format_value(accumulator1.debit, {"fieldtype":"Currency"}) }}</b>
        </td>
        <td class= "text-right">
          <b>{{ frappe.format_value(accumulator2.credit, {"fieldtype":"Currency"}) }}</b>
        </td>
      </tr>
    </table>

    {% set charge = accumulator1.debit - accumulator3.refund %}

    <table class= "table">
      <tr style= "font-weight: bold">
        <td class= "col-xs-8 text-right">
          Total Charge:
          <br>
          Total Payment:
        </td>
        <td class= "col-xs-2 text-right">
          {{ frappe.format_value(charge, {"fieldtype":"Currency"}) }}
          <br>
        </td>
        <td class= "col-xs-2 text-right">
          <br>
          {{ frappe.format_value(accumulator2.credit, {"fieldtype":"Currency"}) }}
        </td>
      </tr>
      <tr style= "font-weight: bold">
        <td class= "col-xs-8 text-right">Total Refund:</td>
        <td class= "col-xs-2 text-right">{{ frappe.format_value(accumulator3.refund, {"fieldtype":"Currency"}) }} </td>
        <td class= "col-xs-2 text-right"> </td>
      </tr>
    </table>
  </div>
</div>

<div class= "row">
    <div class= "col-xs-4">
	<table class= "table text-center">
		<tr>
		<td height="190"></td>
		</tr>
            <tr>
                <td class= "col-xs-4" style= "border-top:none;"><b>{{ doc.customer_id }}</b></td>
            </tr>
            <tr>
                <td style= "border-top:1px solid #d1d8dd;"><b>Customer</b></td>
            </tr>
        </table>
    </div>
    <div class= "col-xs-4">
    </div>
    <div class= "col-xs-4">
        <table class= "table text-center">
        <tr>
            {% set employee = frappe.db.get_value('Employee',{'user_id':frappe.user},'name') %}
		    <td height="190"><img src="{{ frappe.get_doc('Inn Employee Details',{'employee_id': employee}) if employee or '' }}" alt="" align="left" /></td>
		</tr>
		<tr>
            <td class= "col-xs-2" style= "border-top:none;"><b>{{ frappe.db.get_value("User", frappe.user,"full_name") }}</b></td>
        </tr>
        <tr>
            <td style= "border-top:1px solid #d1d8dd;"><b>Staf/Personnel</b></td>
        </tr>
        </table>
    </div>
</div>
