css:
.heading {
    border-bottom: dashed;
    padding-bottom: 0.5rem;
    box-sizing: content-box;
}

.detail {
    border-top: dashed;
    padding: 0.5rem 0;
}

.item-container {
    border-top: thin dashed;
    border-bottom: thin dashed;
    padding: 0 0.5rem 1rem;
}

.item-name {
    line-height: 1rem;
}

.item-quantity {
    display: flex;
    justify-content: flex-end;
}

.item-quantity > div {
    margin-left: 3rem;
}

.line-height-2px {
    line-height: 2px;
}

.footer {
    border-bottom: thin dashed;
    padding: 0.5rem;
}

.footer > div {
    display: flex;
    justify-content: space-between;
}

.nominal {
    text-align: end;
}

.payment {
    border-bottom: thin dashed;
    padding: 0.5rem 0;
}


.remarks {
    white-space: pre-line;
    text-align: center;
}

@media screen {
	.print-format {
		width: 4in;
		padding: 0.25in;
		min-height: 1in;
	}
}
